"""测试数据加载模块"""import loggingimport sysfrom pathlib import Path# 添加项目根目录到路径sys.path.insert(0, str(Path(__file__).parent.parent))from src.data_loader import QADataLoaderdef test_data_loader():    """测试数据加载功能"""    logging.basicConfig(level=logging.INFO)    print("\n" + "="*50)    print("测试数据加载模块")    print("="*50)    # 加载数据    loader = QADataLoader()    qa_pairs = loader.load_data()    # 预处理    processed_data = loader.preprocess_data()    # 打印统计信息    stats = loader.get_statistics()    print("\n=== 数据统计 ===")    print(f"总数据量: {stats['total_count']}")    print(f"分类分布: {stats['categories']}")    print(f"平均问题长度: {stats['avg_question_length']:.2f} 字符")    print(f"平均答案长度: {stats['avg_answer_length']:.2f} 字符")    # 打印前3条数据    print("\n=== 前3条数据示例 ===")    for i, qa in enumerate(processed_data[:3], 1):        print(f"\n第 {i} 条:")        print(f"问题: {qa['question'][:50]}...")        print(f"答案: {qa['answer'][:50]}...")        print(f"分类: {qa['category']}")        print(f"关键词: {qa['keywords']}")    print("\n✅ 数据加载测试完成！")if __name__ == "__main__":    test_data_loader()