"""测试向量化模块"""import loggingimport sysfrom pathlib import Path# 添加项目根目录到路径sys.path.insert(0, str(Path(__file__).parent.parent))from src.data_loader import QADataLoaderfrom src.embedding import EmbeddingModeldef test_embedding():    """测试向量化功能"""    logging.basicConfig(level=logging.INFO)    print("\n" + "="*50)    print("测试向量化模块")    print("="*50)    # 1. 加载数据    print("\n=== 步骤1: 加载数据 ===")    loader = QADataLoader()    loader.load_data()    qa_pairs = loader.preprocess_data()    # 2. 初始化向量化模型    print("\n=== 步骤2: 初始化向量化模型 ===")    embedding_model = EmbeddingModel()    # 3. 生成向量    print("\n=== 步骤3: 生成向量 ===")    embeddings_data = embedding_model.encode_qa_pairs(qa_pairs)    # 4. 保存向量    print("\n=== 步骤4: 保存向量 ===")    embedding_model.save_embeddings(embeddings_data)    # 5. 验证    print("\n=== 向量化结果 ===")    print(f"向量数量: {embeddings_data['total_count']}")    print(f"向量维度: {embeddings_data['embedding_dim']}")    print(f"使用模型: {embeddings_data['model_name']}")    print(f"向量形状: {embeddings_data['embeddings'].shape}")    print("\n✅ 向量化测试完成！")if __name__ == "__main__":    test_embedding()